package com.evanemran.spinnerapp;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.view.View;
import android.widget.AdapterView;
import android.widget.ArrayAdapter;
import android.widget.Spinner;
import android.widget.TextView;

import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity {
    Spinner spinner;
    List<Menu> menuList = new ArrayList<>();
    RecyclerView recycler_menu;
    TextView textView_results;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        spinner = findViewById(R.id.spinner);
        recycler_menu = findViewById(R.id.recycler_menu);
        textView_results = findViewById(R.id.textView_results);


        ArrayAdapter arrayAdapter = ArrayAdapter.createFromResource(
                this,
                R.array.meals,
                R.layout.spinner_text
        );
        spinner.setAdapter(arrayAdapter);
        spinner.setOnItemSelectedListener(spinnerSelectedListener);
    }

    private final AdapterView.OnItemSelectedListener spinnerSelectedListener = new AdapterView.OnItemSelectedListener() {
        @Override
        public void onItemSelected(AdapterView<?> adapterView, View view, int i, long l) {

            menuList.clear();

            textView_results.setText("Showing menu for " + adapterView.getSelectedItem().toString());

            switch (adapterView.getSelectedItem().toString()) {
                case "Starter":
                    menuList.add(new Menu("Roasted stuffed mushrooms", "https://img.taste.com.au/dZ5Cmzox/w643-h428-cfill-q90/taste/2016/11/roasted-stuffed-mushrooms-80629-1.jpeg"));
                    menuList.add(new Menu("Pappadum stacks", "https://img.taste.com.au/NUmSx-Ky/w643-h428-cfill-q90/taste/2016/11/pappadum-stacks-27165-1.jpeg"));
                    menuList.add(new Menu("Bruschetta two ways", "https://img.taste.com.au/Ei1feVfE/w643-h428-cfill-q90/taste/2016/11/bruschetta-two-ways-38105-1.jpeg"));
                    menuList.add(new Menu("Asian-style oysters", "https://img.taste.com.au/jGiAQwqa/w643-h428-cfill-q90/taste/2016/11/asian-style-oysters-71587-1.jpeg"));
                    break;
                case "Appetizer":
                    menuList.add(new Menu("Swedish Meatballs", "https://insanelygoodrecipes.com/wp-content/uploads/2021/05/Swedish-Meatballs-683x1024.webp"));
                    menuList.add(new Menu("Crockpot Buffalo Wings", "https://insanelygoodrecipes.com/wp-content/uploads/2021/09/Buffalo-Chicken-Wings-with-Sauce-683x1024.webp"));
                    menuList.add(new Menu("Hormel Chili Dip", "https://insanelygoodrecipes.com/wp-content/uploads/2021/05/Cheesy-Queso-Dip-with-Tortilla-Chips-683x1024.webp"));
                    menuList.add(new Menu("Cream Cheese Pinwheels", "https://insanelygoodrecipes.com/wp-content/uploads/2021/05/Cheesy-Dip-with-Tomatoes-and-Tortilla-Chips-683x1024.jpg"));
                    break;
                case "Main Course":
                    menuList.add(new Menu("BUTTER CHICKEN", "https://picturetherecipe.com/wp-content/uploads/2020/07/Butter-Chicken-PTR-Featured-395x500.jpg"));
                    menuList.add(new Menu("PALAK PANEER", "https://picturetherecipe.com/wp-content/uploads/2020/04/Palak-Paneer-PTR-Featured-1-395x500.jpg"));
                    menuList.add(new Menu("PERSIAN CHICKEN JOOJEH KEBABS", "https://picturetherecipe.com/wp-content/uploads/2018/05/Persian-Chicken-Kebab-Featured-395x500.jpg"));
                    menuList.add(new Menu("STICKY TAMARIND CHICKEN", "https://picturetherecipe.com/wp-content/uploads/2018/03/Sticky-Tamarind-Chicken-PictureThe-Recipe-Featured-2-395x500.jpg"));
                    break;
                case "Desert":
                    menuList.add(new Menu("Anzac biscuit tarts", "https://img.taste.com.au/7C7p-7mC/w316-h210-cfill-q80/taste/2016/11/anzac-biscuit-tarts-3521-1.jpeg"));
                    menuList.add(new Menu("Chocolate bullets", "https://img.taste.com.au/XohE_xhH/w643-h428-cfill-q90/taste/2016/11/chocolate-bullets-4280-1.jpeg"));
                    menuList.add(new Menu("Ice-cream", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRzVOdYi2JDLrTyhwQkU6oACfyTmWxhPd2bPw&usqp=CAU"));
                    menuList.add(new Menu("Almond and date cake", "https://img.taste.com.au/AGEqDs-1/w316-h210-cfill-q80/taste/2016/11/almond-and-date-cake-94537-1.jpeg"));
                    break;
                case "Drinks":
                    menuList.add(new Menu("Agave Margarita", "data:image/jpeg;base64,"));
                    menuList.add(new Menu("Bourbon Sidecar", "data:image/jpeg;base64,"));
                    menuList.add(new Menu("Oreo Shake", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTE5B56zpinWx4yxMubqXvqX_lKM6MCQkOxig&usqp=CAU"));
                    menuList.add(new Menu("Espresso Martini", "data:image/jpeg;base64,"));
                    break;
            }


            showData(menuList);
        }

        @Override
        public void onNothingSelected(AdapterView<?> adapterView) {

        }
    };

    private void showData(List<Menu> list) {
        recycler_menu.setHasFixedSize(true);
        recycler_menu.setLayoutManager(new GridLayoutManager(MainActivity.this, 2));
        MenuAdapter adapter = new MenuAdapter(MainActivity.this, list);
        recycler_menu.setAdapter(adapter);
    }
}